import React, { useContext } from "react"
import { Button, Dropdown, Modal } from "react-bootstrap";
import { Context } from "index.js";

const CreateClothe = ({ show, handleClose }) => {
    const {clothe} = useContext(Context)
    return (
        <Modal
            show={show}
            onHide={handleClose}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить вещь
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Dropdown>
                    <Dropdown.Toggle>Выберите тип</Dropdown.Toggle>
                    <Dropdown.Menu>
                        {clothe.types.map(type =>
                            <Dropdown.Item key={type.id}>{type.name}</Dropdown.Item>
                        )}
                    </Dropdown.Menu>
                </Dropdown>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={handleClose}>Закрыть</Button>
                <Button variant="outline-success" onClick={handleClose}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    )
};

export default CreateClothe;
