import React, { useState } from "react"
import { Button, Container } from "react-bootstrap";
import CreateBrand from "../components/modals/CreateBrand";
import CreateType from "../components/modals/CreateType";
import CreateClothe from "../components/modals/CreateClothe";

const Admin = (props) => {
  const [brandVisible, setBrandVisible] = useState(false)
  const [typeVisible, setTypeVisible] = useState(false)
  const [clotheVisible, setClotheVisible] = useState(false)
  return (
    <Container className="d-flex flex-column">
      <Button variant={"outline-dark"} className="mt-3 p-2" >Добавить тип</Button>
      <Button variant={"outline-dark"} className="mt-3 p-2" onClick={() => setBrandVisible(true)}>Добавить бренд</Button>
      <Button variant={"outline-dark"} className="mt-3 p-2" onClick={() => setClotheVisible(true)}>Добавить вещь</Button>

      <CreateType show={typeVisible} onHide={() => setTypeVisible(false)}/>
      <CreateBrand show={brandVisible} onHide={() => setBrandVisible(false)}/>
      <CreateClothe show={clotheVisible} onHide={() => setClotheVisible(false)}/>
    </Container>
  )
};

export default Admin;
